</pre> import pandas as pd import numpy as np from sklearn.linear_model import LogisticRegression from sklearn.model_selection import StratifiedKFold from sklearn.preprocessing import StandardScaler from sklearn.metrics import (     accuracy_score,     precision_score,     recall_score,     f1_score,     log_loss,     roc_auc_score,     average_precision_score, )   def get_coefficients_info(model, features, intercept, scaler):     """     Returns the model coefficients as a dictionary that accounts for standardization.     The transformation allows the model to be expressed in terms of the original, unscaled features.     """     # For standardized features, the transformation is z = (x - mean) / std.     # The original expression 'coef * z + intercept' becomes:     # (coef / std) * x + (intercept - coef * mean / std)     transformed_coefs = model.coef_[0] / scaler.scale_     transformed_intercept = intercept - np.sum(         model.coef_[0] * scaler.mean_ / scaler.scale_     )      # Create a mathematical expression for the model using original (unscaled) features     expression_parts = [f"{transformed_intercept:.6f}"]     for feature, coef in zip(features, transformed_coefs):         expression_parts.append(f"{coef:+.6f}*{feature}")     expression = "".join(expression_parts)      # Return a dictionary containing scaling parameters and coefficient information     return {         "expression": expression,         "feature_means": dict(zip(features, scaler.mean_)),         "feature_stds": dict(zip(features, scaler.scale_)),         "original_coefficients": dict(zip(features, model.coef_[0])),         "original_intercept": float(intercept),         "transformed_coefficients": dict(zip(features, transformed_coefs)),         "transformed_intercept": float(transformed_intercept),     }   def perform_cross_validation(df: pd.DataFrame):     """     Loads data, applies standardization, and performs 5-fold stratified cross-validation.      Args:         df: A pandas DataFrame with features and a 'relevance_label' target column.      Returns:         A tuple containing two pandas DataFrames:         - cv_results_df: The mean and standard deviation of evaluation metrics.         - coef_df: The model coefficients for both scaled and unscaled features.     """     # Define and drop irrelevant columns     columns_to_drop = ["doc_id", "query_id", "relevance_score"]     # Drop only the columns that exist in the DataFrame     df = df.drop(columns=[col for col in columns_to_drop if col in df.columns])     df["relevance_label"] = df["relevance_label"].astype(int)      # Define features (X) and target (y)     X = df.drop(columns=["relevance_label"])     features = X.columns.tolist()     y = df["relevance_label"]      # Initialize StandardScaler, model, and cross-validator     scaler = StandardScaler()     N_SPLITS = 5     skf = StratifiedKFold(n_splits=N_SPLITS, shuffle=True, random_state=42)     model = LogisticRegression(C=0.001, tol=1e-2, random_state=42)      # Lists to store metrics for each fold     metrics = {         "Accuracy": [],         "Precision": [],         "Recall": [],         "F1-Score": [],         "Log Loss": [],         "ROC AUC": [],         "Avg Precision": [],     }      # Perform 5-Fold Stratified Cross-Validation     for train_index, test_index in skf.split(X, y):         X_train, X_test = X.iloc[train_index], X.iloc[test_index]         y_train, y_test = y.iloc[train_index], y.iloc[test_index]          # Fit scaler on training data and transform both sets         X_train_scaled = scaler.fit_transform(X_train)         X_test_scaled = scaler.transform(X_test)          # Train the model and make predictions         model.fit(X_train_scaled, y_train)         y_pred = model.predict(X_test_scaled)         y_pred_proba = model.predict_proba(X_test_scaled)[:, 1]          # Calculate and store metrics for the fold         metrics["Accuracy"].append(accuracy_score(y_test, y_pred))         metrics["Precision"].append(precision_score(y_test, y_pred, zero_division=0))         metrics["Recall"].append(recall_score(y_test, y_pred, zero_division=0))         metrics["F1-Score"].append(f1_score(y_test, y_pred, zero_division=0))         metrics["Log Loss"].append(log_loss(y_test, y_pred_proba))         metrics["ROC AUC"].append(roc_auc_score(y_test, y_pred_proba))         metrics["Avg Precision"].append(average_precision_score(y_test, y_pred_proba))      # --- Prepare Results DataFrames ---      # Create DataFrame for cross-validation results     cv_results = {         "Metric": list(metrics.keys()),         "Mean": [np.mean(v) for v in metrics.values()],         "Std Dev": [np.std(v) for v in metrics.values()],     }     cv_results_df = pd.DataFrame(cv_results)      # Retrain on full standardized data to get final coefficients     X_scaled = scaler.fit_transform(X)     model.fit(X_scaled, y)      # Get transformed coefficients for original (unscaled) features     coef_info = get_coefficients_info(model, features, model.intercept_[0], scaler)      # Create DataFrame for coefficients     coef_data = {         "Feature": features + ["Intercept"],         "Coefficient (Standardized)": np.append(model.coef_[0], model.intercept_[0]),         "Coefficient (Original)": np.append(             list(coef_info["transformed_coefficients"].values()),             coef_info["transformed_intercept"],         ),     }     coef_df = pd.DataFrame(coef_data)      return cv_results_df, coef_df   # Perform cross-validation and get the results cv_results_df, coefficients_df = perform_cross_validation(feature_df)  # Print the results print("--- Cross-Validation Results ---") print(cv_results_df.to_string(index=False)) print("\n" + "=" * 40 + "\n") print("--- Model Coefficients ---") print(coefficients_df.to_string(index=False)) <pre>--- Cross-Validation Results ---
